import { createStore } from "vuex";
export default createStore({
    //공통으로 사용할 변수를 정의합니다.
    state:{
        message:'초기값'
    },
    mutations:{ //state의 값을 변경하는 곳입니다.
        update_id(state, payload){
            state.message = payload;
            console.log('[store/index.js]mutations=' + payload);
        }
    },
    actions:{ //mutations = 실행시키는 역할을 합니다.
                    //mutations 이 실행되면 state도 변경됩니다. 비동기 처리합니다.
                     //dispatch('함수명', '전달인자')로 실행 시킬 수 있습니다.
        update(context, getMessage){//update는 dispatch()에서 사용된 첫번째 함수명입니다.
                                                        // 첫번째 매개변수 context는 commit()를 실행시킬 수 있는 객체를 전달 받습니다
                                                        //두번째 매개변수 getMessage는 dispatch()의 '전달인자'입니다.
                                                        //context.commit(mutations에 정의된 함수이름, 함수에 넘길 값)
        context.commit('update_id', getMessage);
        console.log('[store/index.js]actions=' + getMessage);
        }
    }
});
