import { createApp } from 'vue'
import App from './ex18_component_store/App_1.vue';
import store from "./ex18_component_store/store/index.js"

//배치할 요소(#app)와 애플리케이션을 연결하는것을 마운트라고 합니다.
createApp(App).use(store).mount('#app')
